DELETE FROM Civilization_Titles WHERE CivilizationType = 'CIVILIZATION_CVS_SASANIAN' AND GovernmentType IS NOT null AND GovernmentType NOT IN (SELECT GovernmentType FROM Governments);
DELETE FROM Civilization_Titles WHERE CivilizationType = 'CIVILIZATION_CVS_SASANIAN' AND PolicyType IS NOT null AND PolicyType NOT IN (SELECT PolicyType FROM Policies);				

