INSERT INTO Leader_Titles
				(LeaderType, 					GovernmentType, 							PolicyType,				LeaderTitle)
SELECT DISTINCT	'LEADER_CVS_KAWAD_I',			GovernmentType, 							PolicyType,				LeaderTitle
FROM Civilization_Titles WHERE LeaderTitle NOT LIKE "%FEMININE" AND CivilizationType = 'CIVILIZATION_CVS_SASANIAN';

